import axios from 'axios';
import { AllMOVIES, moviesAPI } from '../type/moviesType';

export function getAllMovie() {
  return async (dispatch) => {
    const res = await axios.get(moviesAPI);
    dispatch({
      type: AllMOVIES,
      data: res.data.results,
      pages: res.data.total_pages,
    });
  };
}

export function movieSearch(word) {
  return async (dispatch) => {
    const res = await axios.get(
      `https://api.themoviedb.org/3/search/movie?api_key=${apiKey}&query=${word}`
    );
    dispatch({
      type: AllMOVIES,
      data: res.data.results,
      pages: res.data.total_pages,
    });
  };
}
