import axios from 'axios';
import React, { useState } from 'react';
import ReactPaginate from 'react-paginate';
import { apiKey } from '../Redux/type/moviesType';

export default function PaginationComponent() {
  const [pageCount, setPageCount] = useState(0);
  function handlePageClick(data) {
    getCurrentPage(data.selected + 1);
    console.log(data.selected + 1);
  }

  //Get Current Page
  async function getCurrentPage(page) {
    const res = await axios.get(
      `https://api.themoviedb.org/3/movie/popular?api_key=${apiKey}&language=en-US&page=${page}`
    );
    // setMovies(res.data.results);
    setPageCount(res.data.total_pages);
  }

  return (
    <ReactPaginate
      breakLabel="..."
      nextLabel="next >"
      onPageChange={handlePageClick}
      pageRangeDisplayed={2} // appears the number of pages at the start
      marginPagesDisplayed={2} //The number of pages to display for margins.
      pageCount={pageCount}
      previousLabel="< previous"
      renderOnZeroPageCount={null}
      containerClassName={'pagination justify-content-center p-3'}
      pageClassName={'page-item'}
      pageLinkClassName={'page-link'}
      previousClassName={'page-item'}
      nextClassName={'page-item'}
      breakClassName={'page-item'}
      breakLinkClassName={'page-link'}
      previousLinkClassName={'page-link'}
      nextLinkClassName={'page-link'}
      activeClassName={'active'}
    />
  );
}
