import { Container } from 'react-bootstrap';
import CustomNavBar from './Components/CustomNavBar';
import MovieList from './Components/MovieList';
import axios from 'axios';
import { useState } from 'react';
import { BrowserRouter, Route, Routes } from 'react-router-dom';
import MovieDetails from './Components/MovieDetails';

import { apiKey } from './Redux/type/moviesType';

function App() {
  const [pageCount, setPageCount] = useState(0);

  //Get Current Page
  async function getCurrentPage(page) {
    const res = await axios.get(
      `https://api.themoviedb.org/3/movie/popular?api_key=${apiKey}&language=en-US&page=${page}`
    );
    // setMovies(res.data.results);
    setPageCount(res.data.total_pages);
  }

  //to search in API
  async function filmSearch(word) {
    if (word === '') {
      getAllMovies();
    } else {
      const res = await axios.get(
        `https://api.themoviedb.org/3/search/movie?api_key=${apiKey}&query=${word}`
      );
      // setMovies(res.data.results);
      setPageCount(res.data.total_pages);
    }
  }
  return (
    <div className="color-body">
      <CustomNavBar search={filmSearch} />
      <Container>
        <BrowserRouter>
          <Routes>
            <Route
              path="/"
              element={<MovieList page={getCurrentPage} pageCount={pageCount} />}
            />
            <Route path="/movie/:id" element={<MovieDetails />} />
          </Routes>
        </BrowserRouter>
      </Container>
    </div>
  );
}

export default App;
